<style>
    main {
        display: grid;
        grid-template-columns: 1fr 1fr;
        grid-template-rows: 1fr 1fr;
        
        @media screen and (width <= 57.5rem) {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            height: 100vh;
        }

        h1 {
            font-size: clamp(1.5rem, 5vw, 2rem);
        }
    }

    .image-banner {
        height: max(100vh, 100vh);
        grid-column: 1/2;
        grid-row: 1/3;

        @media screen and (width <= 57.5rem) {
            display: none;
        }

        img {
            height: 100%;
        }
    }

    .message {
        display: flex;
        flex-direction: column;
        justify-content: end;
        gap: 1rem;
        text-align: center;
        grid-column: 2/3;
        grid-row: 1/2;
        padding: clamp(1rem, 1vw, 2rem);

        h3 {
            font-weight: 800;
            font-size: 2.5rem;
            color: #000a;
        }

        h1 {
            font-weight: 900;
            text-transform: uppercase;
            font-size: 2.5rem;
            color: #000;
        }

        p {
            font-weight: 600;
            letter-spacing: .5rem;
            color: #0006;
            text-transform: uppercase;
        }
    }

    .social-networks {
        display: flex;
        flex-direction: column;
        align-items: center;
        margin-top: 4rem;
        gap: 1rem;
        grid-column: 2/3;
        grid-row: 2/3;

        span {
            color: #0009;
        }

        nav {
            display: flex;
            row-gap: 1rem;
            column-gap: 1.5rem;

            svg {
                vertical-align: middle;
                width: 2rem;
                height: 2rem;
            }
        }
    }
</style>